import React from 'react';
import '../styles/Notification.css';

const Notification = ({ message, type, onClose }) => {
  console.log('Notification component rendered. Message:', message, 'Type:', type); // Added console.log

  if (!message) {
    console.log('Notification message is null, returning null.'); // Added console.log
    return null;
  }

  return (
    <div className={`notification ${type}`}>
      <p>{message}</p>
      <button className="close-notification-btn" onClick={onClose}>&times;</button>
    </div>
  );
};

export default Notification;
